<template>
  <div
    v-if="didError"
    :class="`inline-block bg-gray-100 text-center align-middle ${props.class ?? ''}`"
    :style="props.style"
  >
    <div class="flex items-center justify-center w-full h-full">
      <img
        :src="ERROR_IMG_SRC"
        alt="Error loading image"
        :data-original-url="props.src"
      />
    </div>
  </div>
  <img
    v-else
    :src="props.src"
    :alt="props.alt"
    :class="props.class"
    :style="props.style"
    @error="handleError"
  />
</template>

<script setup lang="ts">
import { ref } from 'vue'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

interface Props {
  src?: string
  alt?: string
  class?: string
  style?: string | Record<string, any>
}

const props = defineProps<Props>()

const didError = ref(false)

const handleError = () => {
  didError.value = true
}
</script>
